mapper.update:

UPDATE TB_EVENT
   SET REP_IMG_NO = #{fileNo}		/* 행사이미지번호 */	
 WHERE EVENT_NO = #{eventNo};


UPDATE TB_CMNTY
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE CMNTY_NO = #{cmntyNo}

mapper.insert:

INSERT
  INTO TB_EVENT_APP
  	   (	
  	    EVENT_NO			/* 행사번호(동아리번호) */	
	  , CHR_NO				/* 교적번호(신청자번호) */
	  , APP_YN				/* 승인여부 */
	   )
VALUES (	
		#{eventNo}
	  , #{chrNo}
	  , 'Y'
	   )

mapper.delete:

	DELETE 
  	  FROM TB_EVENT_APP
	 WHERE EVENT_NO = #{eventNo} AND CHR_NO = #{chrNo}
